mapper.select:
SELECT a.content_id 'id', u.user_id, u.avatar, u.name, u.nickname, u.unit, a.is_publish, a.publish_type 'publishType', a.type, an.anthology_id, an.title 'anthologyTitle', IFNULL(UNIX_TIMESTAMP(a.publish_time)*1000,0) 'createTime', IFNULL(UNIX_TIMESTAMP(a.update_time)*1000,0) 'updateTime', a.state 'status', a.title, a.content, (select count(1) from user_action where obj_id = a.content_id and action_type=1) as attentionNum, (select count(1) from user_action where obj_id = a.content_id and action_type=2) as collectNum, (select count(1) from user_action where obj_id = a.content_id and action_type=3) as praiseNum, (select count(1) from user_action where obj_id = a.content_id and action_type=5) as readNum, (SELECT COUNT(1) FROM user_action WHERE `obj_parentid`=a.content_id AND action_type=3) 'answers_praiseNum', (SELECT COUNT(1) FROM sys_comment WHERE parent_id = a.content_id AND comment_type=2) 'answerNum' from sys_content a left join sys_user u on a.create_by = u.user_id left join sys_anthology an on a.anthology_id = an.anthology_id where a.is_publish = 1 and a.is_del = 0
and (a.title like concat('%',#{searchText},'%') or a.content like concat('%',#{searchText},'%') )
and a.type =#{type}
order by a.publish_time limit #{offset},#{limit}
