mapper.insert:
insert into sys_comment
comment_id, parent_id, create_by, create_time, update_time, content, collect_num, praise_num, comment_type, publish_type, publish_time
#{comment_id}, #{parent_id}, #{create_by}, #{create_time}, #{update_time}, #{content}, #{collect_num}, #{praise_num}, #{comment_type}, #{publish_type}, #{publish_time} #{is_del} #{is_publish}

mapper.update:
UPDATE sys_comment
parent_id = #{parent_id}, update_time = #{update_time}, content = #{content}, collect_num = #{collect_num}, praise_num = #{praise_num}, publish_type = #{publish_type}, publish_time = #{publish_time}, is_del = #{is_del}, is_publish = #{is_publish},
WHERE comment_id=#{comment_id}
